mapper.select:
select t.id, t.change_type, t.item_id, t.staff_no as staff_no,b.name as staff_name, create_time, collection_quantity, collection_desc from office_supplies_change_record t left join staff b on t.staff_no=b.id where t.is_del=0 and t.change_type=1
and item_id=#{supplieId,jdbcType=VARCHAR}
and limit ${(pageNo-1)*pageSize},#{pageSize}
limit 0,0

mapper.insert:
insert into office_supplies_change_record (id, change_type, item_id, 
  staff_id, create_time, collection_quantity, 
  collection_desc, is_del, update_time
  )
values (#{id,jdbcType=VARCHAR}, #{changeType,jdbcType=TINYINT}, #{itemId,jdbcType=VARCHAR}, 
  #{staffId,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{collectionQuantity,jdbcType=TINYINT}, 
  #{collectionDesc,jdbcType=VARCHAR}, #{isDel,jdbcType=TINYINT}, #{updateTime,jdbcType=TIMESTAMP}
  )
